sys_script_client.script:
function onLoad()
{
	function _Log(strText)
	{
		Log('DevTools: Format: '+strText);
	}


	function FormatForm()
	{
		var ga = new GlideAjax('DevToolsClientAPI');
		ga.addParam('sysparm_name','GetFormatConfig');
		ga.addParam('sysparm_table',g_form.getTableName());
		ga.addParam('sysparm_sys_id',g_form.getUniqueValue());
		ga.getXML(Response_GetFormatConfig);
		function Response_GetFormatConfig(response)
		{
			//Log('DevToolsClientAPI::GetFormatConfig RESPONSE...');
			var formatconfig = response.responseXML.documentElement.getAttribute('answer');
			try
			{
				formatconfig = JSON.parse(formatconfig);	
			}
			catch(e)
			{
				Log('Could not parse formatconfig. Aborting.');
				return;
			}

			var elementForm = g_form.getFormElement();
			var elements = elementForm.querySelectorAll('.form-control');

			for (var nElement = 0; nElement < elements.length; nElement++)
			{
				var element = elements[nElement];
				var strElementId = ''+element.id;
				var aElementId = strElementId.split('.');
				if (aElementId.length > 0)
				{
					var strColumnId = aElementId[aElementId.length-1];
					var columnconfig = formatconfig[strColumnId];
					if (columnconfig != undefined)
					{
						if (aElementId[0] == 'sys_readonly')
						{
							element.value = FormatValue(element.value,columnconfig);
							element.style.textAlign = 'right';
						}
					}
				}
			}
		}
	}

	FormatForm();

}
