		}
	},

	IncrementProgress: function(nProgress)
	{
		if (this.tracker != null)
		{
			nProgress = GetIntegerValue(nProgress);
			//this.tracker.incrementProgressValue(nProgress);
			//RunScriptInGlobalScope('param1.incrementProgressValue(param2);',this.tracker,nProgress);

			//this.tracker.incrementProgressValue();
			//RunScriptInGlobalScope('param1.incrementProgressValue();',this.tracker);

		}
	},

	SetText: function(strText)
	{
		if (this.tracker != null)
		{		
			strText = GetStringValue(strText);
			//this.tracker.updateDetailMessage(strText);
			//RunScriptInGlobalScope('param1.updateDetailMessage(param2);',this.tracker,strText);
