sys_script_include.script:
function RecordTransporterRecordPack(strPackageSysId,strTable,strSysId)
{
	var grPackage = GetRecord('x_snc_devtools_transporter_package',strPackageSysId);
	if (IsValidRecord(grPackage) == false)
	{
		return false;
	}

	strTable = GetStringValue(strTable);	
	var grRecord = GetRecord(strTable,strSysId);
	if (IsValidRecord(grRecord) == false)
	{
		return false;
	}

	var record = Clone(grRecord);
	if (IsObject(record) == false)
	{
		return false;
	}

	var strContentJson = MakeJson(record);

	//RecordInsertOrUpdate()

	var grTransporterRecord = new GlideRecord('x_snc_devtools_transporter_record');
	grTransporterRecord.initialize();
	grTransporterRecord.payload_sys_id = GetStringValue(grRecord.sys_id);
	grTransporterRecord.payload_table = GetStringValue(grRecord.sys_class_name);
	grTransporterRecord.payload_content = strContentJson;
	grTransporterRecord.package = grPackage.sys_id;
	grTransporterRecord.sys_scope = grPackage.sys_scope;
	grTransporterRecord.sys_package = grPackage.sys_package;
	var strTransportRecordSysId = grTransporterRecord.insert();

	return strTransportRecordSysId;
}
