sys_script_include.script:
function CreateDatabaseIndex(strName, strTable, aColumns, strEmail)
{
	aColumns = GetArrayValue(aColumns);
	for (var nColumn = 0; nColumn < aColumns.length; nColumn++)
	{
		var strColumn = aColumns[nColumn];
		if (IsValidColumn(strTable,strColumn) == false)
		{
			return false;
		}
	}
	var strColumnList = GetStringValue(aColumns);
	gs.include('IndexCreator');
	var bUnique = false;
	var indexCreator = new IndexCreator(strTable, strColumnList, bUnique, strEmail, strName);
	indexCreator.execute();
	return true;
}

