sys_script_include.script:
function DatabaseIndexCreate(strTable, aColumns, bUnique)
{
	aColumns = GetArrayValue(aColumns);
	for (var nColumn = 0; nColumn < aColumns.length; nColumn++)
	{
		var strColumn = aColumns[nColumn];
		if (IsValidColumn(strTable,strColumn) == false)
		{
			return false;
		}
	}
	var strColumnList = GetStringValue(aColumns);
	var bUnique = GetBoolValue(bUnique);

	var strScript = ""
		+"result = false;"
		+"var bResult = gs.include('IndexCreator');"
		+"if (bResult == true)"
		+"{"
		+"  var indexCreator = new IndexCreator(param1, param2, param3, '', '');"
		+"  result = indexCreator.execute();"
		+"}";

	var bResult = RunScriptInGlobalScope(strScript,strTable,strColumnList,bUnique);

	return bResult;
}

