sys_script_include.script:
function ExecuteBusinessRuleShowRecentEvaluatorErrors(current, previous)
{
	var nMinutes = GetIntegerValue(AppGetProperty('ui.show_evaluator_errors.minutes'));
	if (nMinutes < 1)
	{
		return;
	}

	if (gs.isInteractive() == false)
	{
		return;
	}

	var strCurrentUrl = GetCurrentUrl();
	if (strCurrentUrl == GetInstanceURL())
	{
		return;
	}
	if (strCurrentUrl.indexOf('angular.do') != -1)
	{
		return;
	}
	if (strCurrentUrl.indexOf('xmlhttp.do') != -1)
	{
		return;
	}	
	if (strCurrentUrl.indexOf('page_timing') != -1)
	{
		return;
	}	
	if (strCurrentUrl.indexOf('get?') != -1)
	{
		return;
	}		
	if (strCurrentUrl.indexOf('/api/') != -1)
	{
		return;
	}	
	if (strCurrentUrl.indexOf('polarisberg_theme_variables.do?') != -1)
	{
		return;
	}
	if (strCurrentUrl.indexOf('/ds?') != -1)
	{
		return;
	}

	var strOperation = BusinessRuleGetOperation(current);
	if (strOperation != 'query')
	{
		return;
	}

	var transactionCache = new TransactionCacheAPI('DEVTOOLS');
	var strRunningAlready = transactionCache.GetItem('show_eval_errors_running');
	if (strRunningAlready == 'true')
	{
		return;
	}
	transactionCache.SetItem('show_eval_errors_running','true');		

	//gs.addInfoMessage(strCurrentUrl);

	var aErrors = GetRecentEvaluatorErrors(nMinutes);
	//var aErrors = [];

	var strEvaluatorLogUri = 'now/nav/ui/classic/params/target/syslog_list.do%3Fsysparm_query%3Dsys_created_on%253Ejavascript%3Ags.beginningOfLast15Minutes()%255Esource%253DEvaluator%26sysparm_first_row%3D1%26sysparm_view%3DDevTools';

	//gs.addErrorMessage(RenderValue(aErrors));

	if (aErrors.length > 0)
	{
		var error = GetValue(aErrors,0);
		var strMessage = StringFormat('{0} evaluator error(s) have been logged in the last {1} minutes.',aErrors.length,nMinutes);
		strMessage += '<br/>';
		strMessage += 'Most recent error:';
		strMessage += '<br/>';
		strMessage += '_____START____';
		strMessage += '<br/>';
		strMessage += error.message;
		strMessage += '<br/>';
		strMessage += '_____END____';
		strMessage += '<br/>';
		strMessage += HtmlRenderLink(GetInstanceURL()+strEvaluatorLogUri,'Please review the evaluator error log',true);

		strMessage += '<br/>';
		strMessage += strCurrentUrl; 

		gs.addErrorMessage(strMessage);
	}

}
