sys_script_include.script:
function RenderHtmlLinkDirectory()
{
	function GetLinkDirectory()
	{
		var links =
		{
			'Organization':
			[
				{
					table: 'sys_user'
				},
				{
					table: 'sys_user_group'
				},
				{
					table: 'core_company'
				}
			],
			'UI':
			[
				{
					table: 'sys_ui_view'
				},
				{
					table: 'sys_ui_message'
				}
			],
			'Database':
			[
				{
					table: 'sys_db_object'
				},
				{
					table: ''
				}
			],
			'Access and Security':
			[
				{
					table: 'domain'
				},
				{
					table: 'sys_scope_privilege'
				}
			]


		};
		return links;
	}

	function RenderHtmlLinkDirectoryCategory(strCategoryName, aLinks)
	{
		strCategoryName = GetStringValue(strCategoryName);
		aLinks = GetArrayValue(aLinks);

		if (aLinks.length == 0)
		{
			return '';
		}

		var strHtml = '';

		strHtml += '<div>';

		if (strCategoryName != '')
		{
			strHtml += '<h2>'+strCategoryName+'</h2>';
		}
		strHtml += '<ul>';
		for (var nLink = 0; nLink < aLinks.length; nLink++)
		{
			var link = aLinks[nLink];
			if (GetType(link) == 'Object')
			{
				var strTable = GetValue(link,'table');
				if (strTable != '')
				{
					if (IsValidTable(strTable))
					{
						var strUrl = '/nav_to.do?uri='+strTable+'_list.do%3F';
						strHtml += '<li>';
						strHtml += HtmlRenderLink(strUrl, strTable, true);
						strHtml += '</li>';
					}

				}
			}
		}
		strHtml += '</ul>';

		strHtml += '</div>';

		return strHtml;
	}

	var strHtml = '';

	var links = GetLinkDirectory();

	var aCategories = GetKeys(links);
	for (var nCategory = 0; nCategory < aCategories.length; nCategory++)
	{
		var strCategory = aCategories[nCategory];
		//strHtml += ''+ strCategory;

		var strHtmlCategory = RenderHtmlLinkDirectoryCategory(strCategory, links[strCategory]);
		strHtml += strHtmlCategory;
	}

	return strHtml;
}
