sys_script_include.script:
function GetLinkDirectory()
{
	var linkdirectory =
		{
			'Organization':
			[
				{
					table: 'sys_user'
				},
				{
					table: 'sys_user_group'
				},
				{
					table: 'core_company'
				}
			],
			'UI':
			[
				{
					table: 'sys_ui_view'
				},
				{
					table: 'sys_ui_message'
				}
			],
			'Apps':
			[
				{
					table: 'sys_app'
				},
				{
					table: 'sys_plugins'
				},
				{
					table: 'sys_update_set'
				}
			],
			'Database':
			[
				{
					table: 'sys_db_object'
				},
				{
					table: 'sys_db_view'
				},
				{
					table: 'sys_db_view_table'
				},
				{
					table: 'sys_storage_alias',
					view: 'devtools'
				}
			],
			'Access and Security':
			[
				{
					table: 'domain'
				},
				{
					table: 'sys_security_acl'
				},
				{
					table: 'sys_user_role'
				},
				{
					table: 'sys_scope_privilege',
					view: 'devtools'
				}
			],
			'Scripts':
			[
				{
					table: 'sys_script_include'
				},
				{
					table: 'sys_script'
				},
				{
					table: 'sys_ui_action'
				},
				{
					table: 'sysauto_script'
				}
			],
			'CMDB and Asset Management':
			[
				{
					table: 'cmdb_ci'
				},
				{
					table: 'ast_contract'
				}
			],
			'Logs and Audit':
			[
				{
					table: 'syslog',
					filter_only: true
				},
				{
					table: 'sys_audit'
				}
			]
		};

	var eps = new GlideScriptedExtensionPoint().getExtensions('DevTools');
	for (var nEP = 0; nEP < eps.length; nEP++)
	{
		var ep = eps[nEP];
		if (IsValidFunction(ep.GetLinkDirectory))
		{
			var linkdirectory_new = ep.GetLinkDirectory(linkdirectory);
			if (GetType(linkdirectory_new) == 'Object')
			{
				linkdirectory = linkdirectory_new;
			}
		}
	}

	return linkdirectory;
}
