sys_script_include.script:
function GetLinkDirectory()
{
	var linkdirectory =
		{
			'Organization':
			[
				{
					table: 'sys_user'
				},
				{
					table: 'sys_user_group'
				},
				{
					table: 'core_company'
				}
			],
			'Regions':
			[
				{
					table: 'core_country'
				},
				{
					table: 'fx_currency'
				},
				{
					table: 'fx_rate'
				}
			],
			'Apps':
			[
				{
					table: 'sys_app'
				},
				{
					table: 'sys_repo_config'
				},
				{
					table: 'sys_plugins'
				},
				{
					table: 'sys_update_set'
				},
				{
					table: 'sys_remote_update_set'
				}
			],
			'Configuration':
			[
				{
					table: 'sys_properties'
				}
			],
			'Database':
			[
				{
					table: 'sys_db_object'
				},
				{
					table: 'sys_dictionary'
				},
				{
					table: 'sys_dictionary_override'
				},
				{
					table: 'sys_m2m'
				},
				{
					table: 'sys_glide_object'
				},
				{
					table: 'sys_db_view'
				},
				{
					table: 'sys_db_view_table'
				},
				{
					table: 'sys_storage_alias',
					view: 'devtools'
				}
			],
			'Access and Security':
			[
				{
					table: 'domain'
				},
				{
					table: 'sys_security_acl'
				},
				{
					table: 'sys_user_role'
				},
				{
					table: 'sys_scope_privilege',
					view: 'devtools'
				}
			],
			'Scripts':
			[
				{
					table: 'sys_script_include'
				},
				{
					table: 'sys_script'
				},
				{
					table: 'sys_ui_action'
				},
				{
					table: 'sysauto_script'
				}
			],
			'Logs and Audit':
			[
				{
					table: 'syslog',
					filter_only: true
				},
				{
					table: 'sys_audit'
				}
			],
			'UI Views':
			[
				{
					table: 'sys_ui_view'
				},
				{
					table: 'sys_ui_list'
				},
				{
					table: 'sys_ui_list_element'
				},
				{
					table: 'sys_ui_section' 
