sys_script_include.script:
function RecordQueryOrInsert(strTable,query,values,bUpdate)
{
	strTable = GetStringValue(strTable);
	if (IsValidTable(strTable) == false)
	{
		return false;
	}
	if (GetType(query) != 'Object')
	{
		return false;
	}	
	if (GetType(values) != 'Object')
	{
		return false;
	}
	var grRecord = new GlideRecord(strTable);
	var aKeys = GetKeys(query);
	for (var nKey = 0; nKey < aKeys.length; nKey++)
	{
		var strKey = aKeys[nKey];
		if (IsValidColumn(strTable,strKey) == true)
		{
			grRecord.addQuery(strKey,'=',query[strKey]);
		}
	}
	grRecord.query();
	if (grRecord.getRowCount() > 1)
	{
		return false;
	}
	if (grRecord.next())
	{
		if (bUpdate == true)
		{
			GlideRecordSetValues(grRecord,values);
			GlideRecordUpdate(grRecord);
		}
		return GetStringValue(grRecord.sys_id);
	}

	var values = Merge(query,values);

	grRecord.initialize();
	GlideRecordSetValues(grRecord,values);
	var vResult = GlideRecordInsert(grRecord);
	if (IsValidSysId(vResult) == true)
	{
		return vResult;
	}
	return false;
}
