sys_script_include.script:
function RenderValueHex(vValue)
{
	if (vValue == null)
	{
		return '00';
	}

	var strResult = '';
	var strType = GetType(vValue);
	if (strType == 'string' || strType == 'Array')
	{
		for (var nPos = 0; nPos < vValue.length; nPos++)
		{
			if (nPos > 0)
			{
				strResult += ' ';	
			}
			strResult += RenderValueHex(vValue.charCodeAt(nPos)); 
		}
		return strResult;
	}
	if (strType == 'number')
	{
		var strResult = Number(vValue).toString(16);
		strResult = strResult.length == 1 ? "0" + strResult : strResult;
		strResult = strResult.toUpperCase();
		return strResult;
	}
	return '';
}
