function ParseUrl(strUrl)
{
	strUrl = GetStringValue(strUrl);
	var bMatch = strUrl.match(/^(https?\:)\/\/(([^:\/?#]*)(?:\:([0-9]+))?)([\/]{0,1}[^?#]*)(\?[^#]*|)(#.*|)$/);
    if (bMatch == null)
	{
		return false;
	}
	return 	{
        url: strUrl,
        protocol: match[1],
        host: match[2],
        hostname: match[3],
        port: match[4],
        pathname: match[5],
        parameters: match[6],
        anchor: match[7]
    };
}
