sys_script_include.script:
function StartWorker(strName, params)
{
	strName = GetStringValue(strName);

	Debug('StartWorker: START: '+strName);

	var worker = new GlideScriptedHierarchicalWorker();
	worker.setProgressName(strName);
	worker.setScriptIncludeName("x_snc_codesanity.CodeSanityClientAPI");
	worker.setScriptIncludeMethod('ScanApplication');
	worker.putMethodArg('strAppSysId', GetValue(params,'strAppSysId'));
	worker.setBackground(true);
	worker.start();

	var strId =  GetStringValue(worker.getProgressID());		

	Debug('StartWorker: '+strId);

	return strId;

}
