sys_script_include.script:
function CreateUserByEmail(strEmail,aGroups)
{
	strEmail = GetStringValue(strEmail);
	if (IsValidEmail(strEmail) == false)
	{
		return false;
	}

	var aGroups = GetArrayValue(aGroups);

	var grUser = GetUserRecord(strEmail);
	if (IsValidRecord(grUser) == true)
	{
		return false;
	}

	var email = ParseEmail(strEmail);
	if (email == false)
	{
		return false;
	}

	//var strName = email.name;
	//var aName

	var grUser = new GlideRecord('sys_user');
	grUser.initialize();
	grUser.email = strEmail;
	grUser.user_name = email.name;
	grUser.firstname = email.firstname;
	grUser.lastname = email.lastname;
	grUser.active = true;
	var strUserSysId = grUser.insert();

	if (IsValidSysId(strUserSysId) == false)
	{
		return false;
	}


	/*
  // Add user to groups
  for (var i = 0; i < groups.length; i++) {
    var group = new GlideRecord('sys_user_grmember');
    group.initialize();
    group.user = user.sys_id;
    group.group = groups[i];
    group.insert();
  }
  */


  return strUserSysId;
}
