sys_script_include.script:
function SyncDependencyVersions(grDependency)
{
	if (IsValidRecord(grDependency) == false)
	{
		return false;
	}
	if (grDependency.getRecordClassName() != 'sys_package_dependency_m2m')
	{
		return false;
	}

	if (GetStringValue(grDependency.x_snc_devtools_versions) == '')
	{
		grDependency.x_snc_devtools_versions = grDependency.min_version;
	}

	if (GetStringValue(grDependency.min_version) == '')
	{
		var aVersions = GetArrayValue(grDependency.x_snc_devtools_versions);
		grDependency.min_verson = GetValue(aVersions,0);
	}	

	return grDependency;
}
