sys_script_include.script:
function SyncDependencyVersions(grDependency)
{
	if (IsValidRecord(grDependency) == false)
	{
		return false;
	}
	if (grDependency.getRecordClassName() != 'sys_package_dependency_m2m')
	{
		return false;
	}

	var strMinVersion = GetStringValue(grDependency.min_version);
	var strVersions = GetStringValue(grDependency.x_snc_devtools_versions);

	if (strMinVersion == '' && strVersions == '')
	{
		var grPackage = new GlideRecord('sys_package');
		if (grPackage.get(grDependency.dependency))
		{
			strMinVersion = GetStringValue(grPackage.version);
			if (strMinVersion != '')
			{
				grDependency.min_version = strMinVersion;
			}
		}
	}

	if (strVersions == '')
	{
		grDependency.x_snc_devtools_versions = strMinVersion;
	}

	if (strMinVersion == '')
	{
		var aVersions = GetArrayValue(strVersions);
		grDependency.min_version = GetValue(aVersions,0);
	}	

	return grDependency;
}
