sys_script_include.script:
function GetTableFromSysId(strSysId)
{
	if (strSysId == 'global')
	{
		return 'sys_dictionary';
	}
	if (IsValidSysId(strSysId) == false)
	{
		return false;
	}

	var grRecord = GetRecord('sys_metadata',strSysId,true);
	if (grRecord != false)
	{
		return GetStringValue(grRecord.getRecordClassName());
	}

	var grTables = new GlideRecord('sys_db_object');
	grTables.addQuery('super_class','=','3fb471d61b223010edf3642aab4bcbd1'); // sys_id of the task table
	GlideRecordQuery(grTables,true);
	while (grTables.next())
	{
		var strTable  = grTables.getValue('name');
		grRecord = new GlideRecord(strTable);
		if (grRecord.get(strSysId) == true)
		{
			return strTable;
		}
	}

	grRecord = new GlideRecord('sys_user');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}	

	grRecord = new GlideRecord('cmdb_ci');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}

	var grTables = new GlideRecord('sys_db_object');
	grTables.addEncodedQuery('sys_update_nameISNOTEMPTY^nameISNOTEMPTY^nameNOT LIKEts_^nameNOT LIKEpa_scores_^nameNOT LIKEv_^nameNOT LIKE_m2m_');
	grTables.addQuery('name','!=','sys_rollback_incremental');
	grTables.addQuery('name','!=','sys_rollback_sequence');
	grTables.addQuery('name','!=','sn_templated_snip_note_template');
	GlideRecordQuery(grTables,true);
	while (grTables.next())
	{
		var strTable  = grTables.getValue('name');
		grRecord = new GlideRecord(strTable);
		if (grRecord.get(strSysId) == true)
		{
			return strTable;
		}
	}

	return false;
}
