sys_script_include.script:
function GetTableFromSysId(strSysId)
{
	if (strSysId == 'global')
	{
		return 'sys_dictionary';
	}
	if (IsValidSysId(strSysId) == false)
	{
		return false;
	}

	var grRecord = GetRecord('sys_metadata',strSysId,true);
	if (grRecord != false)
	{
		return GetStringValue(grRecord.getRecordClassName());
	}

	var grRecord = GetRecord('sys_package',strSysId,true);
	if (grRecord != false)
	{
		return GetStringValue(grRecord.getRecordClassName());
	}

	var grRecord = GetRecord('domain',strSysId,true);
	if (grRecord != false)
	{
		return GetStringValue(grRecord.getRecordClassName());
	}


	grRecord = new GlideRecord('task');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}	

	grRecord = new GlideRecord('sys_user');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}	

	grRecord = new GlideRecord('sys_user_group');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}	

	grRecord = new GlideRecord('cmdb_ci');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}

	grRecord = new GlideRecord('core_company');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}	

	grRecord = new GlideRecord('ast_contract');
	if (grRecord.get(strSysId) == true)
	{
		return GetStringValue(grRecord.sys_class_name);
	}	


	/* exclude more tables - check out which tables refuse cross scope access */


	var grTables = new GlideRecord('sys_db_object');
	grTables.addNullQuery("extension_model"); // Only want tables that don't extend from anything
	grTables.addEncodedQuery('sys_update_nameISNOTEMPTY^nameISNOTEMPTY^nameNOT LIKEts_^nameNOT LIKEpa_scores_^nameNOT LIKEv_^nameNOT LIKEm2m_');
	grTables.addQuery('name','!=','sys_rollback_incremental');
	grTables.addQuery('name','!=','sys_rollback_sequence');
	grTables.addQuery('name','!=','sn_templated_snip_note_template');
	grTables.addQuery('name','!=','sn_templated_snip_channel');
	GlideRecordQuery(grTables,true);
	while (grTables.next())
	{
		var strTable  = grTables.getValue('name');
		if (IsValidColumn(strTable,'sys_id'))
		{ 
			grRecord = new GlideRecord(strTable);
			grRecord.addQuery('sys_id','=',strSysId);
			GlideRecordQuery(grRecord);
			if (grRecord.next() == true)
			{
				return strTable;
			}
		}
	}

/* Search deleted records?	

	        var grD = new GlideRecord("sys_audit_delete");
        grD.addQuery("documentkey", sysIdToFind);
        grD.query();
        _log("Deleted record results: "+grD.getRowCount());
        while (grD.next()) {
            results.push({
                type: "deleted_record",
                table_name: ""+grD.tablename,
                table_label: ""+grD.tablename.getDisplayValue(),
                table_searched: ""+grD.getRecordClassName(),
                display_value: ""+grD.display_value,
                sys_id: ""+grD.documentkey
            })
        }
*/


	return false;
}
