sys_ui_script.script:
var g_cache = {};

function GetCacheValue(vKey1,vKey2,vKey3)
{
	return GetValue(g_cache,vKey1,vKey2,vKey3);
}

function SetCacheValue(vValue,vKey1,vKey2,vKey3)
{
	return SetValue(vValue,g_cache,vKey1,vKey2,vKey3);
}

(function(){

	function Init()
	{
		Log('DevTools: Init');

		FormatAllFieldsOnPage();

		/*
		SetValue(g_cache,'hallowelt','key1');
		Log(g_cache);

		SetValue(g_cache,'test','incident','INC123');	
		Log(g_cache);

		SetCacheValue('hallo welt level 2','key1','key2');

		Log(GetCacheValue('key1','key2'));
		Log(g_cache);
		*/

		//Log(GetFormatConfig('incident','552c48888c033300964f4932b03eb092'));

		var strUrl = top.window.location.href;
		if (strUrl.indexOf('system_properties_ui.do') != -1)
		{
			var url = ParseUrl(strUrl);
			var strParams = GetValue(url,'parameters');
			var params = ParseUrlParameters(strParams);
			var strCategory = GetValue(params,'sysparm_category');

			if (strCategory == null)
			{
				var strUrlDecoded = decodeURIComponent(strUrl);
				var url = ParseUrl(strUrlDecoded);
				var strParams = GetValue(url,'parameters');
				var params = ParseUrlParameters(strParams);
				var strCategory = GetValue(params,'sysparm_category');
			}

			if (strCategory != null)
			{
				var bResult = ServerRequest('x_snc_devtools.DevToolsClientAPI','SetScopeForSystemPropertiesUI',
				{
					category: strCategory
				});	

				if (bResult == true)
				{
					top.window.location.reload();
				}
			}
		}


	}

	setTimeout(Init, 1000);


})();
