sys_ui_script.script:
function ServerRequest(strAPI,strFunction,vParams/*,callbackResponse*/)
{
	//Log('ServerRequest: '+strAPI+': '+strFunction);
	//Log(vParams);
	//Log(GetType(vParams));

	var ga = new GlideAjax(strAPI);
	ga.addParam('sysparm_name',strFunction);
	var aKeys = GetKeys(vParams);
	for (var nKey = 0; nKey < aKeys.length; nKey++)
	{
		var strParam = aKeys[nKey];
		var strValue = ''+vParams[strParam];
		//Log(''+strParam+': '+strValue);
		ga.addParam('sysparm_'+strParam,strValue);
	}
	ga.getXMLWait();
	var result = ga.getAnswer();
	if (result == 'false')
	{
		return false;
	}
	try
	{
		result = JSON.parse(result);
	}
	catch (e)
	{
		return false;
	}
	return result;
}
