//var g_cache = {};
function GetFormatConfig(strTable,strSysId)
{
	Log('GetFormatConfig');
	/*
	var formatconfig = GetValue(g_cache,strTable,strSysId);
	if (formatconfig != null)
	{
		return formatconfig;
	}
	*/

	var bResult = false;
	var bResponse = false;

	var ga = new GlideAjax('x_snc_devtools.DevToolsClientAPI');
	ga.addParam('sysparm_name','GetFormatConfig');
	ga.addParam('sysparm_table',strTable);
	ga.addParam('sysparm_sys_id',strSysId);
	ga.getXMLWait(Response_GetFormatConfig);
	function Response_GetFormatConfig(response)
	{
		var formatconfig = response.responseXML.documentElement.getAttribute('answer');
		Log('Response_GetFormatConfig:');
		Log('formatconfig:');
		Log(formatconfig);
		if (formatconfig == 'false')
		{
			//Log('Could not retrieve format config.');
			return;
		}
		try
		{
			formatconfig = JSON.parse(formatconfig);
		}
		catch (e)
		{
			Log('Could not parse format config.');
			return;
		}

		/*
		if (GetValue(g_cache,strTable) == null)
		{
			g_cache[strTable] = {};
		}
		g_cache[strRecordClass][strSysId] = formatconfig;
		*/
		bResult = true;
		bResponse = true;
	}	

	//while (bResponse == false) {}

	Log('bResult:');
	Log(bResult);

	if (bResult == false)
	{
		return false;
	}

	Log('formatconfig:');

	Log(formatconfig);


	return formatconfig;
}
